#!/bin/bash

cmake -H. -B/build -DCMAKE_BUILD_TYPE=$BUILD_TYPE -DCMAKE_EXPORT_COMPILE_COMMANDS=ON -DSKIP_PORTABILITY_TEST:BOOL="1" -DBuildTests:BOOL="0" -DWITH_CPP11:BOOL="1" -DCMAKE_BUILD_TYPE:STRING="Release" -DJUST_INSTALL_CEREAL:BOOL="1" -DUTPP_INCLUDE_TESTS_IN_BUILD:BOOL="0" -DDOCTEST_WITH_TESTS:BOOL="0" -DTEST_MODE:STRING="COMPARE" -DBUILD_TESTING:BOOL="0" -DTHREAD_SAFE:BOOL="1" -DCREATURE_WITH_TESTS:BOOL="$WITH_TESTS"